import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Button = (props) => {
  console.log(props)
  const { onClick, text } = props
  return (
    <button onClick={onClick}>
      {text}
      </button>
  )
}

const Statistics = (props) => {
  console.log('Statistics props value is', props)
  const { text, num } = props

  if( sum === 0) {
    return (
      <div>
        <p>No feedback given</p>
      </div>
    )
  }

  return (
    <div>
      <p>{text} {num}</p>
    </div>
  )
}

const App = () => {
  // tallenna napit omaan tilaansa
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
{/*
  let sum = good + neutral + bad
  let avg = (good + (bad * -1)) / (good + neutral + bad)
  let pos = good / (good + neutral + bad) * 100
   */}

  return (
    <div>
      <h1>give feedback</h1>
      <Button
        onClick={() => setGood(good + 1)}
        text='good'
      />
      <Button
        onClick={() => setNeutral(neutral + 1)}
        text='neutral'
      />
      <Button
        onClick={() => setBad(bad + 1)}
        text='bad'
      />
      <h1>statistics</h1>
      <Statistics text="good" num={good}/>
      <Statistics text="neutral" num={neutral} />
      <Statistics text="bad" num={bad} />
    </div>
  )
}

ReactDOM.render(<App />,
  document.getElementById('root')
)
